import React, { Fragment, useState } from 'react';
// import { registar_datos } from '../servicios/contactServicio';

const Contact = () => {
  // crear State
  const [contact, actualizarContact] = useState({
    name: '',
    email: '',
    phoneNumber: '',
    message: '',
  });

  const actualizarState = (e) => {
    actualizarContact({
      ...contact,
      [e.target.name]: e.target.value,
    });
  };

  const { name, email, phoneNumber, message } = contact;

  const submitContact = () => {
    alert('enviando');
  };

  return (
    <Fragment>
      <section id='contact'>
        <h2>CONTACT ME</h2>
        <form onSubmit={submitContact}>
          <div>
            <input
              id='nameTxt'
              name='name'
              type='text'
              placeholder='Name'
              className='border-input'
              onChange={actualizarState}
              value={name}
            />
          </div>
          <div>
            <input
              id='emailTxt'
              name='email'
              type='email'
              placeholder='Email'
              className='border-input'
              onChange={actualizarState}
              value={email}
            />
          </div>
          <div>
            <input
              id='phoneNumberTxt'
              name='phoneNumber'
              type='tel'
              placeholder='Phone Number'
              className='border-input'
              onChange={actualizarState}
              value={phoneNumber}
            />
          </div>
          <div>
            <input
              id='messageTxt'
              name='message'
              type='Message'
              placeholder='Message'
              className='border-input'
              onChange={actualizarState}
              value={message}
            />
          </div>
          <div>
            <button id='btnSend'>SEND</button>
          </div>
        </form>
      </section>
    </Fragment>
  );
};

export default Contact;
